import React from "react";
import ReactDom from "react-dom";
import "./index.css";

const books = [
  {
    img:
      "https://images-na.ssl-images-amazon.com/images/I/51XhQYvFvqL._AC_SX184_.jpg",
    title: "1st title",
    author: "Alemia hep",
    popularity: "hundreds",
  },
  {
    img:
      "https://images-na.ssl-images-amazon.com/images/I/51XhQYvFvqL._AC_SX184_.jpg",
    title: "Harry P",
    author: "jk rowling",
    popularity: "millions of readers",
  },
  {
    img:
      "https://images-na.ssl-images-amazon.com/images/I/51XhQYvFvqL._AC_SX184_.jpg",
    title: "Goosebumps",
    author: "R.L stine",
    popularity: "millions of readers",
  },
];

// CSS

function BookList() {
  return (
    <section className="booklist">
      {books.map((book) => {
        return <Book book={book}></Book>;
      })}
    </section>
  );
}

const Genre = () => <h1>spooky</h1>;

const Book = (props) => {
  const { job, img, title, author, popularity, number } = props.book;
  return (
    <article className="book">
      <img src={img} alt="" />

      <p>{job}</p>
      <p>{title}</p>
      <p>{number}</p>
      <p>{author}</p>
      <p>{popularity}</p>

      <Genre />
      <Price />
    </article>
  );
};

const Price = () => <h2>$5</h2>;

ReactDom.render(<BookList />, document.getElementById("root"));
