import React from "react";
import ReactDom from "react-dom";
import "./index.css";
//setup vars
// const Author = "Amelia Hep";
const title = "new book";
const img =
  "https://images-na.ssl-images-amazon.com/images/I/51XhQYvFvqL._AC_SX184_.jpg";

//component capital case letter
//stateless functional component
//always return JSX

// CSS

function BookList() {
  return (
    <section className="booklist">
      <Book />
    </section>
  );
}

const Book = (props) => {
  console.log(props);
  return (
    <article className="book">
      <img> src={img}</img>
      <Title />
      <h1>{title}</h1>
      <Author />
      <Price />
      <p>{6 + 6}</p>
      <h4>{Author.toUpperCase()}</h4>
    </article>
  );
};

const Image = () => (
  <img
    src="https://images-na.ssl-images-amazon.com/images/I/51XhQYvFvqL._AC_SX184_.jpg"
    alt=""
  />
);

const Price = () => <h2>$5</h2>;

const Title = () => <h1>I dont knoe the title</h1>;

const Author = () => {
  return (
    <h4 style={{ color: "#617d98", fontSize: "0.75rem " }}>Random author</h4>
  );
};

ReactDom.render(<BookList />, document.getElementById("root"));
