import React from "react";
import ReactDom from "react-dom";
import "./index.css";

const firstBook = {
  img:
    "https://images-na.ssl-images-amazon.com/images/I/51XhQYvFvqL._AC_SX184_.jpg",
  title: "1st title",
  author: "Alemia hep",
  popularity: "hundreds",
};

const secondBook = {
  img:
    "https://images-na.ssl-images-amazon.com/images/I/51XhQYvFvqL._AC_SX184_.jpg",
  title: "Harry P",
  author: "jk rowling",
  popularity: "millions of readers",
};

// CSS

function BookList() {
  return (
    <section className="booklist">
      <Book
        img={firstBook.img}
        title={firstBook.title}
        author={firstBook.author}
        popularity={firstBook.popularity}
      />
      <Book
        img={secondBook.img}
        title={secondBook.title}
        author={secondBook.author}
        popularity={secondBook.popularity}
      />
    </section>
  );
}

const Book = (props) => {
  const { img, title, author } = props;
  return (
    <article className="book">
      <img src={props.img} alt="" />

      <p>{job}</p>
      <p>{title}</p>
      <p>{number}</p>
      <p>{author}</p>
      <p>{popularity}</p>
      <Price />
    </article>
  );
};

const Price = () => <h2>$5</h2>;

ReactDom.render(<BookList />, document.getElementById("root"));
